pragma solidity ^0.6.0;
import "@chainlink/contracts/src/v0.6/VRFConsumerBase.sol";

interface Router {
  function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
  external
  payable
  returns (uint[] memory amounts);

    function getAmountsIn(uint amountOut, address[] memory path) external view returns (uint[] memory amounts);
}

interface IPegSwap {
    function swap(uint256 amount, address source, address target) external;
}

interface IERC20 {

    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function allowance(address owner, address spender) external view returns (uint256);

    function transfer(address recipient, uint256 amount) external returns (bool);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);


    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract Coinflip is VRFConsumerBase{

    /*Chainlink setup*/
    bytes32 internal keyHash;
    uint256 internal fee;
    uint256 public randomResult;

    /*Game information and variables*/
    struct Game {
        uint256 index;
        address player1;
        address player2;
        uint256 amount;
        address winner;
    }
    uint256 index;
    mapping(uint256 => Game) gameIndex;
    uint256[] pendingGames;

    address owner;
    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    Router QuickSwap = Router(0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff);
    IPegSwap PegSwap = IPegSwap(0xAA1DC356dc4B18f30C347798FD5379F3D77ABC5b);
    receive() external payable {

    }
    constructor() VRFConsumerBase(
            0x3d2341ADb2D31f1c5530cDC622016af293177AE0, // VRF Coordinator
            0xb0897686c545045aFc77CF20eC7A532E3120E0F1  // LINK Token
        ) public {

        keyHash = 0xf86195cf7690c55907b2b611ebb7343a6f649bff128701cc542f0569e2c549da;
        fee = 0.0001 * 10 ** 18; // 0.0001 LINK
        index = 0;
        owner = msg.sender;
    }

    function get_random_number(uint256 _seed) public returns (bytes32 requestId) {
        require(LINK.balanceOf(address(this)) >= fee, "Not enough LINK - fill contract with faucet");
        return requestRandomness(keyHash, fee, _seed);
    }

    event RandomnessFulfilled(bool success);
    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
        randomResult = randomness;
        emit RandomnessFulfilled(true);
//        for (uint i = 0; i < pendingGames.length; i++) {
//            Game memory game = gameIndex[pendingGames[i]];
//            if (randomResult % 2 == 0) {
//                game.winner = game.player1;
//            }
//            else {
//                game.winner = game.player2;
//            }
//            gameIndex[game.index] = game;
//            payable(owner).transfer(game.amount);
//        }
//        delete pendingGames;
    }


    function create_game() public payable {
        Game memory newGame = Game(index, msg.sender, address(this), msg.value, address(this));
        gameIndex[index] = newGame;
        index += 1;
    }

    function join_game(uint256 _index) external payable {
        Game memory game = gameIndex[_index];
        address[] memory path = get_path();

        require(msg.value == game.amount, "Incorrect amount!");
        require(game.player2 == address(this), "Game already joined");
        require(game.winner == address(this), "Game already played");

        uint256 diff = QuickSwap.getAmountsIn(fee, path)[0];
        QuickSwap.swapETHForExactTokens{value: diff}(fee, path, address(this), block.timestamp + 15);
        IERC20(0x53E0bca35eC356BD5ddDFebbD1Fc0fD03FaBad39).approve(address(PegSwap), fee);
        PegSwap.swap(fee, 0x53E0bca35eC356BD5ddDFebbD1Fc0fD03FaBad39, address(LINK));

        game.player2 = msg.sender;
        game.amount = game.amount - diff;
        gameIndex[_index] = game;

        pendingGames.push(_index);
        get_random_number(block.timestamp);

    }

    function get_path() internal returns (address[] memory _path) {
        address[] memory path = new address[](3);
        path[0] = 0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270; //WMatic
        path[1] = 0x7ceB23fD6bC0adD59E62ac25578270cFf1b9f619; //WETH
        path[2] = 0x53E0bca35eC356BD5ddDFebbD1Fc0fD03FaBad39; //LINK
        return path;
    }

    function get_game_count() external view returns(uint256) {
        return index;
    }

    function get_game_info(uint256 _index) external view returns(uint256 _i, address _player1, address _player2, uint256 _amount, address _winner) {
        Game memory game = gameIndex[_index];
        return (game.index, game.player1, game.player2, game.amount, game.winner);
    }


}
